  #endif
  #if CC_MORPH_TARGET_HAS_NORMAL
    normal.xyz = normal.xyz + getNormalDisplacement(vertexId);
  #endif
  #if CC_MORPH_TARGET_HAS_TANGENT
    tangent.xyz = tangent.xyz + getTangentDisplacement(vertexId);
  #endif
