  layout(set = 0, binding = 3) uniform highp sampler2D cc_shadowMap;
  layout(set = 0, binding = 5) uniform highp sampler2D cc_spotLightingMap;
#endif
#if CC_RECEIVE_SHADOW
vec2 CCGetShadowBias()
{
  #if USE_INSTANCING
    return vec2(a_localShadowBias.x + cc_shadowWHPBInfo.w, a_localShadowBias.y + cc_shadowLPNNInfo.z);
  #elif !USE_BATCHING
    return vec2(cc_localShadowBias.x + cc_shadowWHPBInfo.w, cc_localShadowBias.y + cc_shadowLPNNInfo.z);
  #else
    return vec2(cc_shadowWHPBInfo.w, cc_shadowLPNNInfo.z);
  #endif
}
#endif
#if USE_VERTEX_COLOR
  layout(location = 14) in vec4 a_color;
  layout(location = 2) out lowp vec4 v_color;
#endif
layout(location = 3) out vec3 v_position;
layout(location = 4) out mediump vec3 v_normal;
layout(location = 5) out vec2 v_uv;
#if HAS_SECOND_UV
  layout(location = 6) out mediump vec2 v_uv1;
#endif
#if CC_RECEIVE_SHADOW
  layout(location = 7) out mediump vec2 v_shadowBias;
#endif
#if USE_NORMAL_MAP
  layout(location = 8) out mediump vec4 v_tangent;
#endif
#if HAS_SECOND_UV || CC_USE_LIGHTMAP
  layout(location = 15) in vec2 a_texCoord1;
#endif
#if CC_USE_LIGHTMAP && !USE_BATCHING && !CC_FORWARD_ADD
  layout(location = 9) out vec3 v_luv;
  void CCLightingMapCaclUV()
  {
  #if !USE_INSTANCING
    v_luv.xy = cc_lightingMapUVParam.xy + a_texCoord1 * cc_lightingMapUVParam.z;
    v_luv.z = cc_lightingMapUVParam.w;
  #else
    v_luv.xy = a_lightingMapUVParam.xy + a_texCoord1 * a_lightingMapUVParam.z;
    v_luv.z = a_lightingMapUVParam.w;
  #endif
  }
#endif
void main () {
  StandardVertInput In;
  CCVertInput(In);
  mat4 matWorld, matWorldIT;
  CCGetWorldMatrixFull(matWorld, matWorldIT);
  vec4 pos = matWorld * In.position;
  v_position = pos.xyz;
  v_normal = normalize((matWorldIT * vec4(In.normal, 0.0)).xyz);
  #if CC_RECEIVE_SHADOW
    v_shadowBias = CCGetShadowBias();
  #endif
  #if USE_TWOSIDE
    vec3 viewDirect = normalize(cc_cameraPos.xyz - v_position);
    v_normal *= dot(v_normal, viewDirect) < 0.0 ? -1.0 : 1.0;
  #endif
  #if USE_NORMAL_MAP
    v_tangent.xyz = normalize((matWorld * vec4(In.tangent.xyz, 0.0)).xyz);
    v_tangent.w = In.tangent.w;
  #endif
  v_uv = a_texCoord * tilingOffset.xy + tilingOffset.zw;
  #if SAMPLE_FROM_RT
    v_uv = cc_cameraPos.w > 1.0 ? vec2(v_uv.x, 1.0 - v_uv.y) : v_uv;
  #endif
  #if HAS_SECOND_UV
    v_uv1 = a_texCoord1 * tilingOffset.xy + tilingOffset.zw;
    #if SAMPLE_FROM_RT
      v_uv1 = cc_cameraPos.w > 1.0 ? vec2(v_uv1.x, 1.0 - v_uv1.y) : v_uv1;
    #endif
  #endif
  #if USE_VERTEX_COLOR
    v_color = a_color;
  #endif
  CC_TRANSFER_FOG(pos);
  v_shadowPos = cc_matLightViewProj * pos;
  #if CC_USE_LIGHTMAP && !USE_BATCHING && !CC_FORWARD_ADD
    CCLightingMapCaclUV();
  #endif
  gl_Position = cc_matProj * (cc_matView * matWorld) * In.position;
}

